 FKART.loadDeferredModules = function(){
    $("div[id^='load-snippet-']").each(function() {
         var $container = $(this);
         var url = "/lc" + $(this).attr("data-url");
         url = unescape(url);
         url += ( (url.indexOf("?") !== -1) ? "&" : "?") + "loadDeferred=true";
         $.get(url, function(content) {
             $container.after(content);
             $container.remove();
             FKART.linkTracking.parse();
             init_controllers();
         });
     }); 
