 function prepareGooglePixelFrame(url) {
    ifrm = document.createElement("IFRAME");
    ifrm.setAttribute("src", url);
    ifrm.style.width = 1+"px";
    ifrm.style.height = 1+"px";
    ifrm.frameBorder = 0;
    ifrm.style.display = "none";

    document.body.appendChild(ifrm);
}

